//count the parentheses. Openning for a positive count and closing for a negative  count...
func countParentheses(instructions string) (floor int) {
	floor = 0
	for _, char := range instructions {
		if char == '(' {
			floor++
		} else if char == ')' {
			floor--
		}
	}
	return
}
