//count the parentheses. Openning for a positive count and closing for a negative  count...
func countParentheses(instructions string) (position int) {
	var floor = 0
	for i, char := range instructions {
		if char == '(' {
			floor++
		} else if char == ')' {
			floor--
		}
		if floor == -1 {
			position = i + 1
			return
		}
	}
	return
}
